mapper.update:
UPDATE PRODUCT_PRICE SET VIEW_YN = 'Y' WHERE 1=1 AND (PK_DATE, PRODUCT_ID) IN (
SELECT TO_CHAR(SYSDATE,'YYYYMMDD'), A.PRODUCT_ID FROM PRODUCT_PRICE A , ITEM B WHERE 1=1 AND A.PRODUCT_ID=B.ITEM_CODE AND B.GROUP_CODE='H' AND A.PK_DATE = ( SELECT MAX(PK_DATE) PK_DATE FROM PRODUCT_PRICE WHERE 1=1 AND VIEW_YN ='Y' ORDER BY PK_DATE DESC ) AND A.VIEW_YN = 'Y' AND ROWNUM <= 10
)
